# -*- coding: utf-8 -*-
import os
import sys
import base64
from datetime import datetime
import tkinter as tk
from tkinter import ttk, messagebox, filedialog, PhotoImage
import ezdxf

# Проверка версии Python
if sys.version_info < (3, 4):
    raise RuntimeError("Требуется Python 3.4 или выше (для Windows 7 x86)")

# Настройки папки
DESKTOP = os.path.join(os.path.expanduser("~"), "Desktop")
WORK_FOLDER = os.path.join(DESKTOP, "COXOproScan")
if not os.path.exists(WORK_FOLDER):
    try:
        os.makedirs(WORK_FOLDER)
    except OSError as e:
        raise RuntimeError(f"Не удалось создать рабочую папку: {str(e)}")

class COXOproScan:
    def __init__(self, root):
        self.root = root
        self.params = {}
        self.setup_ui()
        self.reset_settings()
        
    def setup_ui(self):
        self.root.title("COXOproScan v3.4 (Win7 x86)")
        self.root.geometry("850x700")
        self.root.resizable(False, False)
        self.center_window()
        
        # Стили с черными кнопками
        style = ttk.Style()
        style.configure("TLabelFrame", font=('Arial', 9, 'bold'))
        style.configure("Black.TButton", 
                      foreground="white",
                      background="black",
                      font=('Arial', 10, 'bold'),
                      padding=5,
                      borderwidth=1)
        style.map("Black.TButton",
                foreground=[('active', 'white'), ('disabled', 'gray')],
                background=[('active', '#333333'), ('disabled', '#cccccc')])
        
        # Основной контейнер
        main_container = ttk.Frame(self.root)
        main_container.pack(fill=tk.BOTH, expand=True, padx=10, pady=5)
        
        # Левая панель (параметры)
        left_panel = ttk.Frame(main_container, width=400)
        left_panel.pack(side=tk.LEFT, fill=tk.BOTH, expand=False)
        
        # Основные параметры
        main_frame = ttk.LabelFrame(left_panel, text="ОСНОВНЫЕ ПАРАМЕТРЫ", padding=10)
        main_frame.pack(fill=tk.X, pady=5, anchor="w")
        
        self.add_param(main_frame, "scan_length", "Общая длина сканирования (мм):", 0)
        self.add_param(main_frame, "retract", "Величина отвода (мм):", 1)
        self.add_param(main_frame, "speed", "Скорость (мм/мин):", 2)
        
        self.probe_depth_frame = ttk.LabelFrame(left_panel, text="▼ ДОПОЛНИТЕЛЬНЫЕ ПАРАМЕТРЫ ▼", padding=10)
        self.probe_depth_frame.pack(fill=tk.X, pady=5, anchor="w")
        self.add_param(self.probe_depth_frame, "probe_depth", "Глубина зондирования (мм):", 0)

        # Стартовая зона
        start_frame = ttk.LabelFrame(left_panel, text="СТАРТОВАЯ ЗОНА", padding=10)
        start_frame.pack(fill=tk.X, pady=5, anchor="w")
        
        self.add_checkbox(start_frame, "use_start_zone", "Активировать стартовую зону", 0)
        self.add_param(start_frame, "start_zone_length", "Длина зоны (мм):", 1, "use_start_zone")
        self.add_param(start_frame, "start_zone_step", "Шаг (мм):", 2, "use_start_zone")

        # Основная зона
        main_zone_frame = ttk.LabelFrame(left_panel, text="ОСНОВНАЯ ЗОНА", padding=10)
        main_zone_frame.pack(fill=tk.X, pady=5, anchor="w")
        
        self.add_param(main_zone_frame, "main_zone_step", "Шаг (мм):", 0)

        # Конечная зона
        end_frame = ttk.LabelFrame(left_panel, text="КОНЕЧНАЯ ЗОНА", padding=10)
        end_frame.pack(fill=tk.X, pady=5, anchor="w")
        
        self.add_checkbox(end_frame, "use_end_zone", "Активировать конечную зону", 0)
        self.add_param(end_frame, "end_zone_length", "Длина зоны (мм):", 1, "use_end_zone")
        self.add_param(end_frame, "end_zone_step", "Шаг (мм):", 2, "use_end_zone")

        # Кнопки
        btn_frame = ttk.Frame(left_panel)
        btn_frame.pack(fill=tk.X, pady=10, anchor="w")
        
        ttk.Button(
            btn_frame,
            text="СБРОСИТЬ НАСТРОЙКИ",
            style="Black.TButton",
            command=self.reset_settings
        ).pack(side=tk.TOP, fill=tk.X, pady=5)

        ttk.Button(
            btn_frame,
            text="ГЕНЕРИРОВАТЬ G-КОД",
            style="Black.TButton",
            command=self.generate_gcode
        ).pack(side=tk.TOP, fill=tk.X, pady=5)

        ttk.Button(
            btn_frame,
            text="СОЗДАТЬ DXF ДЛЯ ARTCAM",
            style="Black.TButton",
            command=self.create_artcam_file
        ).pack(side=tk.TOP, fill=tk.X, pady=5)

        # Правая панель для изображения с проверкой
        right_panel = ttk.Frame(main_container)
        right_panel.pack(side=tk.RIGHT, fill=tk.BOTH, expand=True, padx=10)
        
        try:
            # ВСТАВЬТЕ ВЕСЬ BASE64 КОД ИЗОБРАЖЕНИЯ ВМЕСТО ЭТОГО ПРИМЕРА
            # (убедитесь что нет переносов строк и посторонних символов)
            image_base64 = ""  # <-- ваш полный код
            
            # Проверка длины base64 строки
            if len(image_base64) < 100:
                raise ValueError("Base64 строка слишком короткая")
                
            # Проверка сигнатуры JPEG
            if not image_base64.startswith("/9j/4"):
                raise ValueError("Неверный формат изображения (ожидается JPEG)")
            
            # Декодирование с проверкой
            image_data = base64.b64decode(image_base64)
            if len(image_data) < 100:
                raise ValueError("Декодированные данные изображения слишком малы")
            
            # Создаем временный файл для проверки
            temp_file = os.path.join(WORK_FOLDER, "temp_image.jpg")
            with open(temp_file, "wb") as f:
                f.write(image_data)
            
            # Проверяем размер файла
            if os.path.getsize(temp_file) < 1024:
                raise ValueError("Файл изображения слишком мал")
            
            # Загружаем изображение
            image = PhotoImage(file=temp_file)
            
            # Создаем метку с изображением
            self.image_label = ttk.Label(right_panel, image=image)
            self.image_label.image = image
            self.image_label.pack(fill=tk.BOTH, expand=True)
            
            # Удаляем временный файл
            os.remove(temp_file)
            
        except Exception as e:
            # Если ошибка - показываем сообщение и заглушку
            error_msg = f"Ошибка загрузки изображения:\n{str(e)}"
            self.image_label = ttk.Label(right_panel, 
                                      text=error_msg,
                                      background="#f0f0f0",
                                      foreground="red",
                                      anchor="center",
                                      padding=10)
            self.image_label.pack(fill=tk.BOTH, expand=True)
            print(error_msg)  # Для отладки

    # ... (остальные методы класса остаются без изменений)

if __name__ == "__main__":
    root = tk.Tk()
    app = COXOproScan(root)
    app.params["probe_depth"].set(20.0)
    root.mainloop()
